
public class operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stubpublic class ConditionalDemo {
			//Ternary Operator
			
			System.out.println(true ?"success":"failure");
			
			
			
			boolean boo=true;
			String result=boo?"success":"failure";
			
			System.out.println(result);
			
			
			
			int num=100%20;
			
			boo=(num==0);//comparison operator - will return true or false
			
			result=boo?"Even number...":"Odd Number...";
			
			System.out.println(result);

	}

}
